import { Component } from "react";
import { v4 as uuid4 } from "uuid";

import "./App.css";
import AppInfo from "../app-info/AppInfo";
import SearchPanel from "../search-panel/SearchPanel";
import AppFilter from "../app-filter/AppFilter";
import MovieList from "../movie-list/movieList";
import MovieAddForm from "../movie-add-form/MovieAddForm";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [
        { name: "Empire of osman", viewers: 811, favourite: false, id: "1" },
        { name: "Ertugrul", viewers: 911, favourite: false, id: "2" },
        { name: "Omar", viewers: 1999, favourite: true, id: "3" },
      ],
    };
  }

  onDelete = (id) => {
    this.setState(({ data }) => ({
      data: data.filter((c) => c.id != id),
    }));
  };

  addMovie = (e, movie) => {
    e.preventDefault();
    this.setState(({ data }) => {
      const newArray = [...data, movie];
      console.log(newArray);
      // const newArray = data;
      // movie.id = uuid4();
      // newArray.push(movie);

      // data = newArray;
      // console.log(newArray);

      // return {
      //   data,
      // };
    });
  };

  render() {
    const { data } = this.state;
    return (
      <div className="app font-monospace">
        <div className="content">
          <AppInfo />
          <div className="search-panel">
            <SearchPanel />
            <AppFilter />
          </div>
          <MovieList data={data} onDelete={this.onDelete} />
          <MovieAddForm addMovie={this.addMovie} />
        </div>
      </div>
    );
  }
}

export default App;
